package com.xworkz.external;

public class Shell {
    public Shell(){
        System.out.println("no-args Shell constructor");
    }

    public void crack(){
        System.out.println("Cracking the shell");
    }

    void collect(){
        System.out.println("Collecting shell from beach");
        shine();
    }

    private void shine(){
        System.out.println("Shell is shining beautifully");
    }
}
